
public class OperatorEx2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

//		논리부정 연산자 - !
		boolean flag = false;
//		가독성
//		클린 코드, ! 쓰면 헷갈릴수도
		System.out.println(flag);
		flag = !flag;
		
		System.out.println(flag);
		System.out.println(!flag);
		
	}

}
