
public class OperatorEx5 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		// 나머지 연산자 - %
		int share = 0;
		int remain = 10 % 8;
		
		share = 10 / 8;
		
		System.out.println("10을 8로 나누면, ");
		System.out.println("몫은" + share + "이고, 나머지는 " + remain + "입니다.");
		
		
	}

}
