
public class OperatorEx7 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		//논리 연산자
		
		int n = 10;
		
		System.out.println(10 >= 20 && 10 <= 20);
		System.out.println(10 >= 20 & 10 <= 20);		
		
		System.out.println(10 >= 20 || 10 <= 20);
		System.out.println(10 >= 20 | 10 <= 20);
	}

}
