
public class CastingEx1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		double d = 100.0;
		int num = 100;
		
		int sum = (int)d + num;
		
		System.out.println("d= " + d);
		System.out.println("num= " + num);
		System.out.println("sum= " + sum);
		
	}

}
