
public class VarTest1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int currentYear = 2020;
		int bornYear = 1996;
		//년 기준으로
		System.out.println(currentYear + "년 기준으로");
		int answerYear = currentYear - bornYear +1;
		// 저의 나이는 25입니다.
		
		
		System.out.println("저의 나이는 "+answerYear+"입니다.");
	
//		+ 더하기 연산자 글자와 숫자를
//		concatenation (연결이라는 기술)
//		숫자+숫자 = 숫자
//		숫자+글자 = 글자
//		글자+글자 = 글자
//		글자 = 문자열 ( 모든 표기법 문자열이 강력함 이세상 숫자를 문자로 표현 가능하기에)
		
		System.out.println(answerYear);
		System.out.println(answerYear+"글자란다");
		System.out.println("나의"+answerYear+1+"글자");
		System.out.println(34+1+"과연");
		System.out.println(34+"1"+"과연");
		
		
	
		
		
	}

}
